import React, { useState } from 'react';
import './doctorLanding.css'; // Import the CSS file

const DoctorLanding = () => {
  // State for scheduled patients with dynamic notes
  const [scheduledPatients, setScheduledPatients] = useState([
    { name: 'Patient A', token: '1234', visits: 7, notes: '' },
    { name: 'Patient B', token: '5678', visits: 3, notes: '' },
    { name: 'Patient C', token: '9101', visits: 1, notes: '' },
  ]);

  const [showMenu, setShowMenu] = useState(false); // State to manage the hamburger menu visibility
  const [newPatientName, setNewPatientName] = useState(''); // State to hold new patient input
  const [newPatientToken, setNewPatientToken] = useState(''); // State for new patient token

  // Handle note change for individual patients
  const handleNoteChange = (index, e) => {
    const updatedPatients = [...scheduledPatients];
    updatedPatients[index].notes = e.target.value; // Update the notes for the specific patient
    setScheduledPatients(updatedPatients);
  };

  // Save notes to the specific patient and clear notes section
  const saveNotes = (index) => {
    const updatedPatients = [...scheduledPatients];
    console.log(`Notes saved for ${updatedPatients[index].name}:`, updatedPatients[index].notes);
    updatedPatients[index].notes = ''; // Clear the notes section after saving
    setScheduledPatients(updatedPatients);
  };

  // Add a new patient to the list
  const addNewPatient = () => {
    if (newPatientName && newPatientToken) {
      setScheduledPatients([
        ...scheduledPatients,
        { name: newPatientName, token: newPatientToken, visits: 1, notes: '' },
      ]);
      setNewPatientName(''); // Clear input fields after adding
      setNewPatientToken('');
    }
  };

  return (
    <div className="doctor-landing">
      {/* Header */}
      <header>
        <div className="logo">Doctor Dashboard</div>
        <nav className="dashboard-nav">
          <a href="#patients">Patients</a>
          <a href="#appointments">Appointments</a>
          <a href="#prescriptions">Prescriptions</a>
        </nav>
        <input type="search" placeholder="Search" />
        <div className="hamburger" onClick={() => setShowMenu(!showMenu)}>
          <div className="line"></div>
          <div className="line"></div>
          <div className="line"></div>
        </div>
        {showMenu && (
          <div className="hamburger-menu">
            <button className="view-profile-btn">View Profile</button>
            <button className="logout-btn">Logout</button>
            <div className="history-icon">
              <img src="./history.jpeg" alt="History" />
              <span>Previous Patients</span>
            </div>
          </div>
        )}
      </header>

      {/* Doctor Info */}
      <section className="doctor-info">
        <div className="profile-pic"></div>
        <div className="doctor-details">
          <h2>Dr. John Doe</h2>
          <p>General Medicine</p>
          <p>Welcome back, Dr. Doe. Here are your scheduled patients for today.</p>
        </div>
      </section>

      {/* Scheduled Patients */}
      <section className="patients-section">
        <h2>Scheduled Patients</h2>
        <ul className="scheduled-patients-list">
          {scheduledPatients.map((patient, index) => (
            <li key={index} className="patient-list-item">
              <div>
                <p>{patient.name} - Token No: {patient.token}</p>
                <p>No. of Visits: {patient.visits}</p>
              </div>
              <div className="notes-container">
                <textarea
                  value={patient.notes}
                  onChange={(e) => handleNoteChange(index, e)}
                  placeholder="Enter notes here..."
                  className="notes-textarea"
                />
                <button type="button" onClick={() => saveNotes(index)} className="save-notes-btn">
                  Save Notes
                </button>
              </div>
            </li>
          ))}
        </ul>

        {/* Add New Patient */}
        <div className="add-patient-section">
          <h3>Add New Patient</h3>
          <input
            type="text"
            value={newPatientName}
            onChange={(e) => setNewPatientName(e.target.value)}
            placeholder="Patient Name"
            className="add-patient-input"
          />
          <input
            type="text"
            value={newPatientToken}
            onChange={(e) => setNewPatientToken(e.target.value)}
            placeholder="Token No."
            className="add-patient-input"
          />
          <button type="button" onClick={addNewPatient} className="add-patient-btn">
            + Add Patient
          </button>
        </div>
      </section>

      {/* Patient Statistics */}
      <section className="stats-section">
        <h2>Patient Statistics</h2>
        <div className="stats-grid">
          <div className="stat-item">
            <h3>Visits Today</h3>
            <p>3</p> {/* Hardcoded for example, replace with actual logic if needed */}
          </div>
          <div className="stat-item">
            <h3>Visits This Month</h3>
            <p>25</p> {/* Hardcoded for example, replace with actual logic if needed */}
          </div>
        </div>
      </section>
    </div>
  );
};

export default DoctorLanding;
