import React, { useState } from 'react';
import './PatientProfile.css';

const PatientProfile = () => {
  // Dummy patient data
  const [patient, setPatient] = useState({
    name: 'Cool Guy',
    age: 26,
    sex: 'Male',
    contact: '9876543210',
    case: 'Flu and Cold', // Case field
    note: 'Cool, hes sick, thats why hes cool; cold to be exact.',
  });

  const [editMode, setEditMode] = useState(false);
  const [editedNote, setEditedNote] = useState(patient.note);
  const [editedCase, setEditedCase] = useState(patient.case);

  // Toggle edit mode
  const handleEditClick = () => {
    setEditMode(true);
  };

  // Save updated case and notes
  const handleSaveClick = () => {
    setPatient({
      ...patient,
      note: editedNote,
      case: editedCase,
    });
    setEditMode(false);
  };

  return (
    <div className="patient-profile">
      <h2>{patient.name}'s Profile</h2>
      <img src="/patient-profile-image.jpg" alt="Patient" />

      <div className="info">
        <p><strong>Age:</strong> {patient.age}</p>
        <p><strong>Sex:</strong> {patient.sex}</p>
        <p><strong>Contact:</strong> {patient.contact}</p>
      </div>

      {/* Editable case field */}
      <div className="case-field">
        <p><strong>Case:</strong></p>
        {editMode ? (
          <textarea
            value={editedCase}
            onChange={(e) => setEditedCase(e.target.value)}
          />
        ) : (
          <p>{patient.case}</p>
        )}
      </div>

      {/* Editable notes field */}
      <div className="notes-field">
        <p><strong>Notes:</strong></p>
        {editMode ? (
          <textarea
            value={editedNote}
            onChange={(e) => setEditedNote(e.target.value)}
          />
        ) : (
          <p>{patient.note}</p>
        )}
      </div>

      {/* Edit and Save buttons */}
      <div className="actions">
        {editMode ? (
          <button className="save-button" onClick={handleSaveClick}>
            Save
          </button>
        ) : (
          <button className="edit-button" onClick={handleEditClick}>
            <span role="img" aria-label="edit">✏️</span>
          </button>
        )}
      </div>
    </div>
  );
};

export default PatientProfile;
