import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './PatientProfile.css';  // Import CSS for styling

const PatientProfile = () => {
  const { id } = useParams(); // Get the patient ID from the URL
  const patients = [
    { name: 'Adam Messy', age: 26, sex: 'Male', visits: 5, note: '' },
    { name: 'Celine Aluista', age: 22, sex: 'Female', visits: 3, note: '' },
  ];

  const patient = patients[id]; // Get the selected patient data based on the ID

  const [note, setNote] = useState(patient.note); // State for editable notes

  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };

  return (
    <div className="patient-profile">
      <h2>{patient.name}'s Profile</h2>
      <p><strong>Age:</strong> {patient.age}</p>
      <p><strong>Sex:</strong> {patient.sex}</p>
      <p><strong>Number of Visits:</strong> {patient.visits}</p>
      <div>
        <h3>Notes</h3>
        <textarea
          value={note}
          onChange={handleNoteChange}
          rows="4"
          cols="50"
          placeholder="Enter notes here..."
        />
      </div>
      <button className="prescribe-button">Prescribe Medicine</button>
    </div>
  );
};

export default PatientProfile;
