import React, { useState } from 'react';

const PatientProfile = ({ patient, onClose }) => {
  const [note, setNote] = useState(patient.note);
  const [isMedicineModalOpen, setIsMedicineModalOpen] = useState(false);
  const [searchedMedicine, setSearchedMedicine] = useState('');

  const openMedicineModal = () => setIsMedicineModalOpen(true);
  const closeMedicineModal = () => setIsMedicineModalOpen(false);

  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };

  return (
    <div className="patient-profile">
      <img src={`${patient.name.toLowerCase().replace(' ', '-')}.jpg`} alt={patient.name} />
      <h2>{patient.name}</h2>
      <div className="info">
        <p><strong>Age:</strong> {patient.age} Years</p>
        <p><strong>Sex:</strong> {patient.sex}</p>
        <p><strong>Contact Number:</strong> 9876543210</p> {/* Replace with actual number */}
        <textarea
          rows="4"
          placeholder=""
          value={note}
          onChange={handleNoteChange}
        />
      </div>
      <button className="prescribe-button" onClick={openMedicineModal}>Prescribe Medicine</button>

      {/* Modal for prescribing medicine */}
      {isMedicineModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Prescribe Medicine</h3>
            <input
              type="search"
              placeholder="Search Medicine"
              value={searchedMedicine}
              onChange={(e) => setSearchedMedicine(e.target.value)}
            />
            <button onClick={closeMedicineModal}>Prescribe</button>
            <button className="close-btn" onClick={closeMedicineModal}>Close</button>
          </div>
        </div>
      )}

      <button className="close-btn" onClick={onClose}>Close Profile</button>
    </div>
  );
};

export default PatientProfile;
