import React, { useState } from 'react';
import './PatientProfile.css'; // Import the CSS file for styling

const PatientProfile = ({ patient, onClose }) => {
  const [note, setNote] = useState(patient.note || 'Click to add notes.');
  const [isEditing, setIsEditing] = useState(false);

  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };

  const handlePrescribeMedicine = () => {
    // Logic for prescribing medicine (e.g., open modal)
    console.log('Prescribe medicine for', patient.name);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>{patient.name}</h2>
        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
      <div className="profile-info">
        <p><strong>Age:</strong> {patient.age}</p>
        <p><strong>Sex:</strong> {patient.sex}</p>
        <p><strong>Number of Visits:</strong> {patient.visits}</p>
      </div>
      <div className="notes-section">
        <h3>Notes</h3>
        {isEditing ? (
          <textarea value={note} onChange={handleNoteChange} />
        ) : (
          <p>{note}</p>
        )}
        <button onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? 'Save' : 'Edit'}
        </button>
      </div>
      <div className="prescribe-section">
        <button onClick={handlePrescribeMedicine}>Prescribe Medicine</button>
      </div>
    </div>
  );
};

export default PatientProfile;
