import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';  // Importing calendar styles
import './Dashboard.css';  // Your existing styles

const Dashboard = () => {
  const [date, setDate] = useState(new Date());
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isMedicineModalOpen, setIsMedicineModalOpen] = useState(false);
  const [note, setNote] = useState('Click the pen to write notes.');
  const [isEditing, setIsEditing] = useState(false);
  const [searchedMedicine, setSearchedMedicine] = useState('');

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);
  const openMedicineModal = () => setIsMedicineModalOpen(true);
  const closeMedicineModal = () => setIsMedicineModalOpen(false);

  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };

  const handleSearchChange = (event) => {
    setSearchedMedicine(event.target.value);
  };

  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo">
          <h2>Mediczen™</h2>
        </div>
        <input type="search" placeholder="Search" />
        <nav>
          <ul>
            <li><a href="#">Dashboard</a></li>
            <li><a href="#">Patients</a></li>
            <li><a href="#">Billing</a></li>
          </ul>
          <h3>Support</h3>
          <ul>
            <li><a href="#">Help Center</a></li>
            <li><a href="#">Settings</a></li>
          </ul>
        </nav>
      </aside>

      <main className="main-content">
        <header className="dashboard-header">
          <div className="greeting">
            <h1>Good Morning, Dr. A!</h1>
            <p>I hope you're in a good mood because there are 56 patients waiting for you</p>
          </div>
          <div className="profile">
            <img src="doctor-profile.jpg" alt="Doctor Profile" />
          </div>
        </header>

        <div className="content-wrapper">
          <section className="patients-section">
            <div className="patient-list-header">
              <h2>Patients</h2>
              <a href="#">See All</a>
            </div>
            <table className="patients-table">
              <thead>
                <tr>
                  <th>Name (Age, Gender)</th>
                  <th>Token No.</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>
                    <img src="adam.jpg" alt="Adam Messy" />
                    Adam Messy<br />Male, 26 Years
                  </td>
                  <td><button className="token-link" onClick={openModal}>#123456</button></td>
                </tr>
                <tr>
                  <td>
                    <img src="celine.jpg" alt="Celine Aluista" />
                    Celine Aluista<br />Female, 22 Years
                  </td>
                  <td><button className="token-link" onClick={openModal}>#985746</button></td>
                </tr>
              </tbody>
            </table>
          </section>

          {/* Calendar Section */}
          <section className="calendar-section">
            <Calendar 
              onChange={setDate} 
              value={date} 
              className="react-calendar" 
            />
            <p>Selected Date: {date.toDateString()}</p>
          </section>
        </div>

        {/* Modal for patient details */}
        {isModalOpen && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Patient Details</h2>
              <div className="notes-section">
                <h3>Notes</h3>
                {isEditing ? (
                  <textarea value={note} onChange={handleNoteChange} />
                ) : (
                  <p>{note}</p>
                )}
                <button onClick={() => setIsEditing(!isEditing)}>
                  {isEditing ? 'Save' : 'Edit'}
                </button>
              </div>
              <div className="prescribe-section">
                <button onClick={openMedicineModal}>Prescribe Medicine</button>
              </div>
              <button className="close-btn" onClick={closeModal}>Close</button>
            </div>
          </div>
        )}

        {/* Modal for prescribing medicine */}
        {isMedicineModalOpen && (
          <div className="modal-overlay">
            <div className="modal smaller">
              <h3>Prescribe Medicine</h3>
              <input
                type="search"
                placeholder="Search Medicine"
                value={searchedMedicine}
                onChange={handleSearchChange}
              />
              <button onClick={closeMedicineModal}>Prescribe</button>
              <button className="close-btn" onClick={closeMedicineModal}>Close</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
