import React, { useState } from 'react';
import './PatientProfile.css';  // Updated CSS file

const PatientProfile = ({ patient }) => {
  const [showModal, setShowModal] = useState(false);
  const [note, setNote] = useState(patient.note || '');

  // Handle opening/closing modal
  const handlePrescribe = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  // Handle note change
  const handleNoteChange = (e) => {
    setNote(e.target.value);
  };

  return (
    <div className="patient-profile-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo">
          <h2>Mediczen™</h2>
        </div>
        <input type="search" placeholder="Search" />

        <nav>
          <ul>
            <li><a href="#" className="active">Patients</a></li>
            <li><a href="#">Billing</a></li>
          </ul>
          <h3>Support</h3>
          <ul>
            <li><a href="#">Help Center</a></li>
            <li><a href="#">Settings</a></li>
          </ul>
        </nav>
      </aside>

      {/* Main content */}
      <div className="patient-profile-content">
        <h2>{patient.name}'s Profile</h2>
        <img src={`${patient.name.toLowerCase().replace(' ', '-')}.jpg`} alt={patient.name} />

        <div className="info">
          <p><strong>Age:</strong> {patient.age} Years</p>
          <p><strong>Sex:</strong> {patient.sex}</p>
          <p><strong>Contact:</strong> {patient.contact || 'N/A'}</p>
        </div>

        <textarea 
          placeholder="Enter notes here..." 
          value={note} 
          onChange={handleNoteChange} 
        />
        
        <button className="prescribe-button" onClick={handlePrescribe}>
          Prescribe Medicine
        </button>

        {/* Modal */}
        {showModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h3>Prescribe Medicine</h3>
              <input type="text" placeholder="Enter medicine name" />
              <input type="text" placeholder="Dosage" />
              <input type="text" placeholder="Duration" />
              <button className="close-btn" onClick={closeModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PatientProfile;
